      }

      next() {
        this.index += 1;
        return this.collection\[this.index\];
      }

      current() {
        return this.collection\[this.index\];
      }

      reset() {
        this.index = 0;
      }

      hasNext() {
          return (this.index + 1 < this.collection.length );
      }
    }

    const arr = \[1,2,3,4,5\];

    const arrayIterator = new Iterator(arr);

    console.log(arrayIterator.first());


    while (arrayIterator.hasNext()) {
      console.log(arrayIterator.next());
    }
  <!-- endtab -->
{% endtabbed_codeblock %}

